def trick_score(trick: set[tuple[str, str]], trump_suit: str) -> int:
    return (lambda ranks, suits: exec('raise ValueError("Trick must contain exactly four valid cards")') if len(trick) != 4 or any(card[1] not in suits for card in trick) or any(card[0] not in ranks for card in trick) else exec('raise TypeError("Invalid suit provided")') if trump_suit not in suits else sum(map(lambda rank_suit: ranks[rank_suit[0]][0] if rank_suit[1] == trump_suit else ranks[rank_suit[0]][-1], trick)))({"7": [0], "8": [0], "9": [14, 0], "10": [10], "Jack": [20, 2], "Queen": [3], "King": [4], "Ace": [11]}, {"Spades ", "Diamonds", "Hearts", "Clubs"})
